```

ç„¶è€Œä½¿ç”¨è¿™ç§æ–¹æ³•ä¼šæœ‰ä¸€äº›éšè—çš„å‘ï¼Œå®ƒèƒ½æ­£ç¡®å¤„ç†çš„å¯¹è±¡åªæœ‰ `Number`, `String`, `Boolean`, `Array`, æ‰å¹³å¯¹è±¡ï¼Œå³é‚£äº›èƒ½å¤Ÿè¢« json ç›´æ¥è¡¨ç¤ºçš„æ•°æ®ç»“æ„ã€‚


### è‡ªå·±é€ è½®å­
ä¸‹é¢æˆ‘ä»¬ç»™å‡ºä¸€ä¸ªç®€å•çš„è§£å†³æ–¹æ¡ˆï¼Œå½“ç„¶è¿™ä¸ªæ–¹æ¡ˆæ˜¯å‚è€ƒåˆ«äººçš„æ–¹å¼æ¥å®ç°çš„ã€‚å¸Œæœ›å¯¹å¤§å®¶æœ‰ç”¨ã€‚

```javascript
var clone = (function() {
	//è¿™ä¸ªæ–¹æ³•ç”¨æ¥è·å–å¯¹è±¡çš„ç±»å‹ è¿”å›å€¼ä¸ºå­—ç¬¦ä¸²ç±»å‹ "Object RegExp Date Array..."
	var classof = function(o) {
		if (o === null) {
			return "null";
		}
		if (o === undefined) {
			return "undefined";
		}
		// è¿™é‡Œçš„Object.prototype.toStringå¾ˆå¯èƒ½ç”¨çš„å°±æ˜¯Object.prototype.constructor.name
		// è¿™é‡Œä½¿ç”¨Object.prototype.toStringæ¥ç”Ÿæˆç±»å‹å­—ç¬¦ä¸²
		var className = Object.prototype.toString.call(o).slice(8, -1);
		return className;
	};

	//è¿™é‡Œè¿™ä¸ªå˜é‡æˆ‘ä»¬ç”¨æ¥å­˜å‚¨å·²ç»ä¿å­˜è¿‡çš„å±æ€§ï¼Œç›®çš„åœ¨äºå¤„ç†å¾ªç¯å¼•ç”¨çš„é—®é¢˜
	var references = null;

	//é‡åˆ°ä¸åŒç±»å‹çš„å¯¹è±¡çš„å¤„ç†æ–¹å¼
	var handlers = {
		//æ­£åˆ™è¡¨è¾¾å¼çš„å¤„ç†
		'RegExp': function(reg) {
			var flags = '';
			flags += reg.global ? 'g' : '';
			flags += reg.multiline ? 'm' : '';
			flags += reg.ignoreCase ? 'i' : '';
			return new RegExp(reg.source, flags);
		},
		//æ—¶é—´å¯¹è±¡å¤„ç†
		'Date': function(date) {
			return new Date(+date);
		},
		//æ•°ç»„å¤„ç† ç¬¬äºŒä¸ªå‚æ•°ä¸ºæ˜¯å¦åšæµ…æ‹·è´
		'Array': function(arr, shallow) {
			var newArr = [],
			i;
			for (i = 0; i < arr.length; i++) {
				if (shallow) {
					newArr[i] = arr[i];
				} else {
					//è¿™é‡Œæˆ‘ä»¬é€šè¿‡referenceæ•°ç»„æ¥å¤„ç†å¾ªç¯å¼•ç”¨é—®é¢˜
					if (references.indexOf(arr[i]) !== -1) {
						continue;
					}
					var handler = handlers[classof(arr[i])];
					if (handler) {
						references.push(arr[i]);
						newArr[i] = handler(arr[i], false);
					} else {
						newArr[i] = arr[i];
					}
				}
			}
			return newArr;
		},
